import React, { useState } from "react";
import { Container, Row, Col, Form, InputGroup, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import { db } from "../../firebase/config";
import "./foooter.css";

const FooterComponent = () => {
  const [email, setEmail] = useState("");
  const [loader, setLoader] = useState();

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    db.collection("newsletter")
      .add({ email: email })
      .then(() => {
        setLoader(false);
        alert("Welcome to the club! We promise not to spam you.");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });
    setEmail("");
  };

  return (
    <>
      <Container
        className="w-100 h-25  mt-3 text-left"
        style={{ backgroundColor: "#f5f5f5" }}
      >
        <Row className="m-1 pb-4" xs={1} md={1} lg={1}>
          <Col>
            <Form>
              <Form.Label
                style={{ fontSize: 12 }}
                className=" text-uppercase"
              >
                Subscribe for the latest releases
              </Form.Label>
              <Form.Text>
                <p>
                  Stay informed about the latest style updates and product
                  launches. Learn more about email and our Privacy Policy.
                </p>
              </Form.Text>
            </Form>
          </Col>
          <Col>
            <Form.Row onSubmit={handleSubmit}>
              <Col>
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="email@example.com"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    style={{
                      borderLeftColor: "transparent",
                      borderTopColor: "transparent",
                      borderRightColor: "transparent",
                      borderBottomColor: "transparent",
                    }}
                  />
                  <InputGroup.Append>
                    <Button
                      type="submit"
                      style={{
                        backgroundColor: loader ? "rgba(2,2,0)" : "transparent",
                        borderColor: "transparent",
                        color: "#1f1f1f",
                      }}
                    >
                      submit
                    </Button>
                  </InputGroup.Append>
                </InputGroup>
              </Col>
            </Form.Row>
          </Col>
        </Row>
      </Container>
      <Container fluid className="mt-4">
        <Row className="text-center" xs={1} md={3} lg={3}>
          <Col>
            <Link to="/myaccount">My Account</Link>
          </Col>
          <Col>
            <Link to="/stockist">Stockist</Link>
          </Col>
          <Col>
            <Link to="/">Policy</Link>
          </Col>
        </Row>
      </Container>
      <Container>
        <Row xs={1} md={1} lg={1} className="pt-5 m-4">
          <Col>
            <p className="text-muted">
              Website by
              <a
                className="pl-2 text-muted"
                href="https://jonmichaelnarvaez.com"
                rel="noopener noreferrer"
                target="__blank"
                style={{ textDecoration: "none" }}
              >
                B-Boy Creative
              </a>
            </p>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default FooterComponent;
