import React, { useState } from "react";
import { Container, Row, Col, Form, Button, Spinner } from "react-bootstrap";
import { Link } from "react-router-dom";
import { db } from "../../firebase/config";
import "../../App.css";

const FooterComponent = () => {
  const [email, setEmail] = useState("");
  const [loader, setLoader] = useState();

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    db.collection("newsletter")
      .add({ email: email })
      .then(() => {
        setLoader(false);
        alert("Welcome to the club! We promise not to spam you.");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });
    setEmail("");
  };

  return (
    <Container className="w-100 h-25 pt-5 text-center">
      <Row className="m-1 pb-4" xs={1} md={1} lg={1}>
        <Col>
          <Form>
            <Form.Label className="p-2">Newsletter</Form.Label>
          </Form>
        </Col>
        <Col>
          <Form.Row
            xs={3}
            md={1}
            lg={1}
            className="mx-auto"
            onSubmit={handleSubmit}
          >
            <Col>
              <Form.Control
                size="sm"
                type="email"
                placeholder="Enter email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={{
                  borderLeftColor: "transparent",
                  borderTopColor: "transparent",
                  borderRightColor: "transparent",
                }}
              />
            </Col>
            <Col>
              <Button variant="link" size="sm">
                <Spinner an/>
              </Button>
            </Col>
          </Form.Row>
        </Col>
      </Row>
      <Row xs={4} md={4} lg={4}>
        <Col>
          <Link to="/myaccount">Account</Link>
        </Col>
        <Col>
          <Link>Location</Link>
        </Col>
        <Col>
          <Link>Policy</Link>
        </Col>
        <Col>
          <Link>Account</Link>
        </Col>
      </Row>
      <Row xs={1} md={1} lg={1}>
        <Col>
          <p className="text-muted p-5 text-center fs-1">
            SALA Investment Co. Company
          </p>
        </Col>
      </Row>
    </Container>
  );
};

export default FooterComponent;
