import React from "react";
import StripeCheckout from "react-stripe-checkout";
import JMLogo from "../../assets/jmlogo.png";
import axios from "axios";
import { loadStripe } from "@stripe/stripe-js";

const stripePrommise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);


const StripeCheckoutButton = ({ price }) => {
  co
  const handleClick = async (event) => {
    // Get Stripe.js instance
    const stripe = await stripePromise;

    // Call your backend to create the Checkout Session
    const response = await fetch('/create-checkout-session', { method: 'POST' });

    const session = await response.json();

    // When the customer clicks on the button, redirect them to Checkout.
    const result = await stripe.redirectToCheckout({
      sessionId: session.id,
    });

    if (result.error) {
      // If `redirectToCheckout` fails due to a browser or network
      // error, display the localized error message to your customer
      // using `result.error.message`.
    }
  };
  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: "priceForStripe",
        token,
      },
    })
      .then((response) => {
        alert("Payment Successful");
      })
      .catch((error) => {
        console.log("Payment error:", JSON.parse(error));
        alert(
          "Ooops, there was an issue with your payment. Please check your information for errors"
        );
      });
  };
  return <button role="link">Checkout</button>;
};

export default StripeCheckoutButton;
